import{_ as e,c as i,o as a,ae as l}from"./chunks/framework.BJfjNJL0.js";const m=JSON.parse('{"title":"人工扒谱委托业务","description":"","frontmatter":{"title":"人工扒谱委托业务","tags":["MIDI","业务服务"],"categories":"野生技术","cover":"https://cdn.guichushijie.cn/img/bapuman.webp","abbrlink":40296,"date":"2024-11-04T18:16:45.000Z"},"headers":[],"relativePath":"docs/diy-midi.md","filePath":"docs/diy-midi.md"}'),d={name:"docs/diy-midi.md"};function r(o,t,s,n,c,h){return a(),i("div",null,t[0]||(t[0]=[l('<h1 id="人工midi扒谱委托业务" tabindex="-1">人工MIDI扒谱委托业务 <a class="header-anchor" href="#人工midi扒谱委托业务" aria-label="Permalink to &quot;人工MIDI扒谱委托业务&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>人工扒谱委托比较耗时，推荐加入金胡萝卜会员获取200+midi伴奏包，一些流行的热曲我会第一时间扒出共享，性价比极高。详情请到介绍页面查看：<a href="/vitepress/docs/vip/vip.html">🥕金胡萝卜会员介绍页面</a> 您可以去<a href="/vitepress/docs/freemidi.html">免费配布页面</a>下载一些免费配布的查看扒谱质量</p></div><p>本业务是 <code>人工耳扒</code> ，将一首歌曲主旋律利用宿主软件人工制作成单轨MIDI，可供UTAU、VOCALOID、Synthesizer V、Ace studio、网易X Studio、DiffSinger、袅袅、Sharpkey、DeepVocal等虚拟歌姬制作软件通用的 <code>.mid</code> 文件。</p><p>比起一些软件提供的Ai识别生成MIDI，人工耳扒在音高上更准确、不会跑调、不会突然高8度等问题</p><h2 id="人工-vs-ai识别" tabindex="-1">人工 VS AI识别 <a class="header-anchor" href="#人工-vs-ai识别" aria-label="Permalink to &quot;人工 VS AI识别&quot;">​</a></h2><p>比起一些软件提供的Ai识别生成MIDI，人工耳扒在音高上更准确、不会跑调、不会突然高8度等问题</p><table tabindex="0"><thead><tr><th style="text-align:left;"><strong>人工耳扒MIDI</strong></th><th style="text-align:left;"><strong>AI识别生成MIDI</strong></th></tr></thead><tbody><tr><td style="text-align:left;">✅ 音高更准确：绝对音准，助理调教</td><td style="text-align:left;">❌音高容易识别错误（识别成和声、高低八度等）</td></tr><tr><td style="text-align:left;">✅ 节奏更准确：人工对节拍，让你的调教不拖拍</td><td style="text-align:left;">❌节奏混乱、填词时痛苦折磨</td></tr><tr><td style="text-align:left;">✅ 提前对轨：midi、伴奏对轨，减轻或避免混音对轨工作</td><td style="text-align:left;">❌midi伴奏节奏混乱，对不上</td></tr><tr><td style="text-align:left;">✅ 更高质量的伴奏：使用最新最好的算法提取或寻找原版伴奏交付</td><td style="text-align:left;">❌效果不好、人声清除不干净</td></tr></tbody></table><h2 id="工作流程" tabindex="-1">工作流程 <a class="header-anchor" href="#工作流程" aria-label="Permalink to &quot;工作流程&quot;">​</a></h2><ol><li>QQ/微信联系，发送原曲试听</li><li>确认需求：单人声轨、和声轨、节奏轨、间奏轨、乐器轨（明显的）</li><li>报价</li><li>制作</li><li>交付</li></ol><h2 id="交付内容" tabindex="-1">交付内容 <a class="header-anchor" href="#交付内容" aria-label="Permalink to &quot;交付内容&quot;">​</a></h2><ol><li>原曲： {歌曲名}.wav</li><li>MIDI文件： {扒谱日期} {BPM} 胡萝卜须儿 扒谱 {歌曲名}.mid</li><li>分离的原曲伴奏文件：1_{歌曲名}_(Instrumental).wav</li><li>分离的原曲人声文件：1_{歌曲名}_(Vocals).wav</li></ol><h2 id="定价规则" tabindex="-1">定价规则 <a class="header-anchor" href="#定价规则" aria-label="Permalink to &quot;定价规则&quot;">​</a></h2><p>最终报价=基础价格+轨道+难度价格</p><ol><li>基础价格：￥25 即单人声轨（短于1min收取半价）</li><li>轨道价格：￥5~￥20 和声轨、节奏轨、间奏轨、乐器轨（明显的）酌情添加</li><li>歌曲难度价格：￥5~￥10 根据歌曲的难度，例如快歌、rap、全曲人声占比等酌情加价</li></ol><h2 id="委托方式" tabindex="-1">委托方式 <a class="header-anchor" href="#委托方式" aria-label="Permalink to &quot;委托方式&quot;">​</a></h2><p>QQ：2806864996</p><p>微信：HUU176</p>',17)]))}const f=e(d,[["render",r]]);export{m as __pageData,f as default};
